#include <bits/stdc++.h>
int main(){char str[105];int a[105],s[105],T,slen,len,lena,t;scanf("%d", &T);while (T--){slen = 0;scanf("%s", str);len = strlen(str);lena = 0; for (int i = 2; i < len; i++) a[lena++] = str[i] - '0'; int flag = 1, cnt = 0; for (int j = 0; j < lena; j++){cnt = 0;for (int i = 0; i < lena; i++){t = a[lena - i - 1];a[lena - i - 1] = (a[lena - i - 1]*2+cnt)%10;cnt = (t*2)/10;}s[slen++] = cnt;} for (int i = 0; i < lena; i++)if (a[i] > 0){flag = 0;break;} if (flag){printf("0.");for (int i = 0; i < slen; i++) printf("%d", s[i]);printf("\n");}else puts("NO");}return 0;}